changeSet.sql:
CREATE TABLE orders ( id uuid not null, table_id int not null, customer_name varchar(30), status varchar(10) not null, total_price float, created_at timestamp(3), updated_at timestamp(3) );
CREATE TABLE order_items ( order_id uuid not null, menu_id uuid not null );
CREATE TABLE menu ( id uuid not null, item_name varchar(30), status varchar(10) not null, price float, description text, created_at timestamp(3), updated_at timestamp(3) );
